import { Component } from '@angular/core';

@Component({
  selector: 'app-root',
  templateUrl: './app.component.html',
  styleUrls: ['./app.component.css']
})
export class AppComponent {
  title = 'web';
}
<!DOCTYPE html>
<html lang="pl">

<head>
  <meta charset="UTF-8">
  <meta name="viewport" content="width=device-width, initial-scale=1">
  <title>Your Hotel Name</title>
  <link rel="stylesheet" href="app.component.css">
</head>

<body>
  <div class="navbar">
    <div class="logo">Your Hotel Name</div>
    <div class="menu-icon">&#9776;</div>
    <ul class="menu-links">
      <li><a href="#" class="menu-link">Request a quote</a></li>
      <li><a href="#" class="menu-link">Call now</a></li>
      <li><a href="#" class="menu-link">Get directions</a></li>
    </ul>
    <div class="menu-accommodation">
      <a href="#" class="menu-link">Accommodation</a>
    </div>
  </div>

  <script>
    const menuIcon = document.querySelector('.menu-icon');
    const menuLinks = document.querySelector('.menu-links');

    menuIcon.addEventListener('click', () => {
      menuLinks.classList.toggle('show');
    });
  </script>
</body>

</html>
